CREATE OR REPLACE FUNCTION obter_resp_aval_dinamica ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_resp_w		varchar(10);
ie_forma_resp_aval_din_w	varchar(1);

BEGIN
 
select	coalesce(max(ie_forma_resp_aval_din), 'M') 
into STRICT	ie_forma_resp_aval_din_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_forma_resp_aval_din_w = 'U') then 
	select	obter_pessoa_fisica_usuario(nm_usuario_p, 'C') 
	into STRICT	cd_pessoa_resp_w 
	;
elsif (ie_forma_resp_aval_din_w = 'R') then 
	select	cd_pessoa_resp 
	into STRICT	cd_pessoa_resp_w 
	from	avf_tipo_avaliacao 
	where	nr_sequencia = nr_sequencia_p;
end if;
 
return	cd_pessoa_resp_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
