SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resp_laudo_agenda ( nr_seq_agenda_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(60);
nr_sequencia_w	bigint;
dt_liberacao_w	timestamp;


BEGIN 
 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	max(nr_sequencia) 
	into STRICT	nr_sequencia_w 
	from	laudo_paciente a 
	where	nr_atendimento	= nr_atendimento_p 
	and	nr_prescricao		= 
		(SELECT max(a.nr_prescricao) 
		from	prescr_procedimento b, 
			prescr_medica a 
		where	a.nr_prescricao	= b.nr_prescricao 
		and	a.nr_atendimento	= nr_atendimento_p 
		and	b.cd_procedimento	= 
			(select cd_procedimento 
			from agenda_paciente 
			where	nr_sequencia	= nr_seq_agenda_p));
	select	max(dt_liberacao) 
	into STRICT	dt_liberacao_w 
	from	laudo_paciente 
	where	nr_sequencia	= nr_sequencia_w;
 
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
		select	max(substr(obter_nome_pf(b.cd_pessoa_fisica),1,60)) 
		into STRICT	ds_retorno_w	 
		from	laudo_paciente a, 
				pessoa_fisica b 
		where	a.cd_medico_Resp	= b.cd_pessoa_fisica		 
		and		a.nr_sequencia		= nr_sequencia_w;
	end if;	
	 
end if;
 
return ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_resp_laudo_agenda ( nr_seq_agenda_p bigint, nr_atendimento_p bigint) FROM PUBLIC;

