CREATE OR REPLACE FUNCTION obter_resp_pagto_tit_pagar (nr_titulo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p

	PF - Pessoa f√≠sica
	PJ - Pessoa_juridica
	A - Ambas (PF ou PJ)

*/
cd_cgc_w			varchar(255);
cd_pessoa_fisica_w		varchar(255);
ds_retorno_w			varchar(255);


BEGIN

select	max(cd_cgc),
	max(cd_pessoa_fisica)
into STRICT	cd_cgc_w,
	cd_pessoa_fisica_w
from	titulo_pagar_resp_pagto
where	nr_titulo	= nr_titulo_p;

if (ie_opcao_p = 'PF') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p = 'PJ') then
	ds_retorno_w	:= cd_cgc_w;
elsif (ie_opcao_p = 'PJ') then
	ds_retorno_w	:= coalesce(cd_cgc_w, cd_pessoa_fisica_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
