SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resp_proj (nr_seq_projeto_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2) := 'N';


BEGIN

if (coalesce(nm_usuario_p,'X') <> 'X') and (coalesce(nr_seq_projeto_p,0) > 0) then
	begin
	select	max(ds_retorno)
	into STRICT	ds_retorno_w
	from (
		SELECT	'S' ds_retorno -- se não existe registro
		from 	gpi_resp_projeto
		where	nr_seq_proj_gpi = nr_seq_projeto_p
		having	count(*) = 0
		
union all

		SELECT	'S' ds_retorno -- se existe para o usuário
		from 	gpi_resp_projeto
		where	nr_seq_proj_gpi = nr_seq_projeto_p
		and	nm_usuario_lib = nm_usuario_p
		
union all

		select	'N' ds_retorno
		 LIMIT 1) alias6;

	if (coalesce(ds_retorno_w,'N') = 'N') then
		begin

		select	max(ds_retorno)
		into STRICT	ds_retorno_w
		from (	SELECT	'S' ds_retorno -- se o usuário é o gestor funcional
			from	gpi_projeto c,
				usuario a
			where	a.cd_pessoa_fisica = c.cd_gestor_funcional
			and	c.nr_sequencia = nr_seq_projeto_p
			and	a.nm_usuario = nm_usuario_p
			
union all

			SELECT 'S' ds_retorno -- se o usuário é o gestor do projeto
			from	gpi_projeto c,
				usuario a
			where	a.cd_pessoa_fisica = c.cd_pf_gestor
			and	c.nr_sequencia = nr_seq_projeto_p
			and	a.nm_usuario = nm_usuario_p
			
union all

			select 'N'
			) alias3;
		end;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
