CREATE OR REPLACE FUNCTION obter_resp_rep_processo_adep (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_prescritor_w	varchar(60);


BEGIN 
 
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then 
 
	select	substr(obter_dados_horario(nr_seq_horario_p,'UP'),1,60) 
	into STRICT	nm_prescritor_w 
	;
 
elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then 
 
	select	substr(obter_usuario_prescritor(nr_prescricao_p),1,60) 
	into STRICT	nm_prescritor_w 
	;
 
end if;
 
return nm_prescritor_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
