CREATE OR REPLACE FUNCTION obter_restricao_avaliacao (nr_seq_tipo_aval_p bigint) RETURNS varchar AS $body$
DECLARE


ie_usuario_restrito_w		varchar(100)	:= 'N';


BEGIN

if (nr_seq_tipo_aval_p IS NOT NULL AND nr_seq_tipo_aval_p::text <> '') then
	begin

	select	coalesce(max(ie_usuario_restrito),'N')
	into STRICT		ie_usuario_restrito_w
	from		med_tipo_avaliacao
	where		nr_sequencia	= nr_seq_tipo_aval_p;

	end;
end if;

return	ie_usuario_restrito_w;

end;
$body$
LANGUAGE PLPGSQL
