SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_restricao_exame_mmed ( nr_prescricao_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ie_liberado_w 		varchar(1) := 'S';		
ie_atend_particular_w	varchar(1);
ie_conta_aberta_w	varchar(1);

BEGIN
 
-- verificar se é atendimento particular 
select	OBTER_SE_PRESCR_PARTICULAR(nr_atendimento_p, nr_prescricao_p) 
into STRICT	ie_atend_particular_w
;
 
if (ie_atend_particular_w = 'S') then 
	begin 
	-- verificar se a conta está aberta 
	select	OBTER_SE_ATEND_CONTA_ABERTO(nr_atendimento_p) 
	into STRICT	ie_conta_aberta_w 
	;
	 
	if (ie_conta_aberta_w = 'S') then 
		ie_liberado_w	:=	'N';
	end if;
	end;
end if;
 
return	ie_liberado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
