CREATE OR REPLACE FUNCTION obter_restricao_pepo ( nr_seq_pepo_p bigint, nr_atendimento_pepo_p bigint, cd_pessoa_fisica_pepo_p text, nr_cirurgia_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_pepo_w	pepo_cirurgia.nr_sequencia%type;


BEGIN

if (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then

	if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then

		select	max(nr_seq_pepo)
		into STRICT	nr_seq_pepo_w
		from	cirurgia
		where	nr_cirurgia = nr_cirurgia_p;

		if (nr_seq_pepo_p = nr_seq_pepo_w) then
			return 'S';
		else
			return 'N';
		end if;

	elsif (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		if (nr_atendimento_pepo_p = nr_atendimento_p) then
			return 'S';
		else
			return 'N';
		end if;
	elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		if (cd_pessoa_fisica_pepo_p = cd_pessoa_fisica_p) then
			return 'S';
		else
			return 'N';
		end if;
	else
		return 'N';
	end if;
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
