CREATE OR REPLACE FUNCTION obter_restricao_san_exame ( nr_seq_exame_p bigint, nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ie_restricao_w		varchar(1);
nr_seq_producao_w	bigint;
nr_seq_reserva_w	bigint;
nr_seq_res_prod_w	bigint;
nr_seq_transfusao_w	bigint;
nr_seq_doacao_w		bigint;
ds_retorno_w		varchar(255) := '';
ie_retorno_w		varchar(1);	
	

BEGIN

if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then

	select 	coalesce(max(ie_restricao_exame),'N')
	into STRICT	ie_restricao_w
	from	san_exame
	where	nr_sequencia	= nr_seq_exame_p;

	select	max(nr_seq_producao),
		max(nr_seq_reserva),
		max(nr_seq_transfusao),
		max(nr_seq_res_prod),
		max(nr_seq_doacao)
	into STRICT	nr_seq_producao_w,
		nr_seq_reserva_w,
		nr_seq_transfusao_w,
		nr_seq_res_prod_w,
		nr_seq_doacao_w
	from	san_exame_lote
	where	nr_sequencia = nr_seq_lote_p;
	
	if ((ie_restricao_w = 'B') and ((nr_seq_reserva_w IS NOT NULL AND nr_seq_reserva_w::text <> '') or (nr_seq_transfusao_w IS NOT NULL AND nr_seq_transfusao_w::text <> '') or (nr_seq_doacao_w IS NOT NULL AND nr_seq_doacao_w::text <> ''))) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(796966);
	elsif (((nr_seq_producao_w IS NOT NULL AND nr_seq_producao_w::text <> '') or (nr_seq_res_prod_w IS NOT NULL AND nr_seq_res_prod_w::text <> '')) and (ie_restricao_w = 'P')) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(796967);
	else
		ds_retorno_w := 'S';
	end if;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
