CREATE OR REPLACE FUNCTION obter_restricao_sms_pessoa (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w	varchar(255);
ie_tipo_doc_w	varchar(010);


BEGIN

select	max(ie_tipo_doc)
into STRICT	ie_tipo_doc_w
from	pessoa_fisica_corresp
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and 	ie_tipo_corresp		= 'MCel';

if (ie_tipo_doc_w = 'N') then
	ds_mensagem_w	:= wheb_mensagem_pck.get_texto(309247); -- NÃ£o SMS
end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
