CREATE OR REPLACE FUNCTION obter_rest_regra_osmolaridade ( qt_osmolaridade_p nut_pac.qt_osmolaridade_total%type, ie_via_administracao_p nut_pac.ie_via_administracao%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
C = Cor da regra
M = Mensagem da regra
*/
ds_cor_w			rep_consiste_osmolaridade.ds_cor%type;
ds_mensagem_w		rep_consiste_osmolaridade.ds_mensagem%type;
ds_retorno_w		varchar(255);


BEGIN

if (qt_osmolaridade_p IS NOT NULL AND qt_osmolaridade_p::text <> '') then

	select	max(ds_mensagem),
			max(ds_cor)
	into STRICT	ds_mensagem_w,
			ds_cor_w
	from	rep_consiste_osmolaridade
	where	qt_osmolaridade_p between qt_inicial and qt_final
	and		((coalesce(ie_via_administracao::text, '') = '') or (ie_via_administracao = ie_via_administracao_p));

	if (ie_opcao_p = 'C') then
		ds_retorno_w := ds_cor_w;
	elsif (ie_opcao_p = 'M') then
		ds_retorno_w := ds_mensagem_w;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
