CREATE OR REPLACE FUNCTION obter_resultado_cpis ( qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);

/*
0 a 6	= Baixa probabilidade de pneumonia relacionada a ventilação mecânica
7 a 17	= Alta probabilidade de pneumonia relacionada a ventilação mecânica
*/
BEGIN
ds_retorno_w	:= '';
if (qt_pontuacao_p between 0 and 6) then
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309688); --'Baixa probabilidade de pneumonia relacionada a ventilação mecânica';
elsif (qt_pontuacao_p between 7 and 16) then
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309691); --'Alta probabilidade de pneumonia relacionada a ventilação mecânica';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
