CREATE OR REPLACE FUNCTION obter_resultado_cruzade ( qt_ponto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN
ds_retorno_w	:= '';

if (qt_ponto_p IS NOT NULL AND qt_ponto_p::text <> '') then
	if (qt_ponto_p	> 50) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309676); --'risco muito elevado';
	elsif (qt_ponto_p	> 40) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309678); --'risco elevado';
	elsif (qt_ponto_p	> 30) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309679); --'risco moderado';
	elsif (qt_ponto_p	> 20) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309695); --'risco baixo';
	else
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309699); --'risco muito baixo';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
