SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resultado_escala_eif ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
/*	ds_retorno_w:
'D'	= Descrição do resultado
'T'	= Valor total do resultado
'S'	= Sequência do resultado
'R'	= Verifica se tos os itens da regra estão selecionados.
*/
qt_total_w		double precision;
nr_seq_item_w		bigint;
nr_seq_result_w		bigint;
ie_resultado_w		varchar(2) := 'N';
qt_resultado_w		bigint := 0;
qt_registro_w		bigint := 0;

C01 CURSOR FOR
	SELECT	a.nr_seq_item
	from	eif_escala_result_regra a,
		eif_escala_result b,
		escala_eif c
	where	a.nr_seq_result = b.nr_sequencia
	and	c.nr_sequencia = nr_sequencia_p
	and	b.nr_seq_escala = c.nr_seq_escala	
	order by a.nr_seq_item;


BEGIN


select	coalesce(sum(CASE WHEN a.ie_resultado='S' THEN b.qt_pontos_pos  ELSE b.qt_pontos_neg END ),0)
into STRICT	qt_total_w
from	escala_eif_item a,
	eif_escala_item b
where	a.nr_seq_escala = nr_sequencia_p
and	b.nr_sequencia = a.nr_seq_item
and	coalesce(a.ie_titulo,'N') = 'N';

if (upper(ie_opcao_p) = 'D') or (upper(ie_opcao_p) = 'R') then

	open C01;
	loop
	fetch C01 into	
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		select 	a.ie_resultado
		into STRICT	ie_resultado_w
		from	escala_eif_item a,
			eif_escala_item b
		where	a.nr_seq_escala = nr_sequencia_p
		and	b.nr_sequencia = a.nr_seq_item
		and	a.nr_Seq_item = nr_seq_item_w;

		if (ie_resultado_w = 'S') then
			qt_resultado_w := qt_resultado_w + 1;	
		end if;
		qt_registro_w := qt_registro_w + 1;
		end;
	end loop;
	close C01;
	
	if ( qt_resultado_w = qt_registro_w ) and (qt_registro_w <> 0) then
		ie_resultado_w := 'S';
	else	
		ie_resultado_w := 'N';
	end if;
	
	if (upper(ie_opcao_p) = 'R') then
		ds_retorno_w := ie_resultado_w;
	else
	
		select	max(coalesce(a.ds_resultado,0))
		into STRICT	ds_retorno_w
		from 	eif_escala_result a,
				escala_eif b
		where	b.nr_sequencia = nr_sequencia_p
		and	a.nr_seq_escala = b.nr_seq_escala
		and	qt_total_w between a.qt_pontos_min and a.qt_pontos_max
		and	(((ie_resultado_w = 'S') and exists (	SELECT 	1	
								from	eif_escala_result_regra x
								where	x.nr_seq_result = a.nr_sequencia))
			or ((ie_resultado_w = 'N') and not exists ( 	SELECT 	1	
								from	eif_escala_result_regra x
								where	x.nr_seq_result = a.nr_sequencia)));	
	end if;
	
elsif (upper(ie_opcao_p) = 'T') then
	ds_retorno_w	:= qt_total_w;

elsif (upper(ie_opcao_p) = 'S') then
	
	Select 	nr_seq_result
	into STRICT	ds_retorno_w
	from	escala_eif
	where	nr_sequencia = nr_sequencia_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
