SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resultado_escala_mews (nr_atendimento_p bigint, qt_horas_cons_p bigint) RETURNS varchar AS $body$
DECLARE

ds_mews_w varchar(255);


BEGIN

	SELECT obter_pontuacao_mews(nr_atendimento_p,qt_horas_cons_p) || ' ' || wheb_mensagem_pck.GET_TEXTO(811191) || ' ' || x.dt_avaliacao
	INTO STRICT ds_mews_w
	FROM ESCALA_MEWS x
	WHERE	 x.nr_atendimento = nr_atendimento_p
	AND 	 coalesce(x.dt_inativacao::text, '') = ''
	AND	 (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
	AND 	 x.nr_sequencia = (SELECT MAX(nr_sequencia)
				   FROM escala_mews y
				   WHERE y.nr_atendimento = x.nr_atendimento);

return	ds_mews_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
