CREATE OR REPLACE FUNCTION obter_resultado_mif (qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
ds_retorno_w := '';
if (qt_pontuacao_p <= 18) then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309393); -- Ajuda total (indivíduo >= 0%)
elsif (qt_pontuacao_p between 19 and 36) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309394); -- Ajuda máxima (indivíduo >= 25%)
elsif (qt_pontuacao_p between 37 and 54) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309395); -- Ajuda (indivíduo >= 50%)
elsif (qt_pontuacao_p between 55 and 72) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309396); -- Ajuda mínima (indivíduo >= 75%)
elsif (qt_pontuacao_p between 73 and 90) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309397); -- Supervisão
elsif (qt_pontuacao_p between 91 and 108) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309398); -- Independência modificada (ajuda técnica)
elsif (qt_pontuacao_p >= 109) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309399); -- Independência completa (em segurança, em tempo normal)
end if;



return	ds_retorno_w;

end;
