CREATE OR REPLACE FUNCTION obter_resultado_pe ( nr_seq_evolucao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_item_w			varchar(80);
ds_resultado_W			varchar(255);
nr_Seq_item_w			bigint;
ds_retorno_w			varchar(2000)	:= '';


C01 CURSOR FOR
	SELECT  y.nr_seq_item
	from 	pe_item_resultado y,
		pe_evolucao_resultado a
	where 	a.nr_seq_resultado  	= y.nr_sequencia
	and 	a.nr_seq_evolucao	= nr_seq_evolucao_p;


BEGIN
OPEN C01;
LOOP
FETCH C01 into
	nr_Seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_Seq_item_w IS NOT NULL AND nr_Seq_item_w::text <> '') then
		begin
		select  x.ds_item,
			y.ds_resultado
		into STRICT	ds_item_w,
			ds_resultado_w
		from 	pe_item_resultado y,
			pe_item_examinar x,
			pe_evolucao_resultado a
		where 	x.nr_sequencia		= y.nr_seq_item
		and 	a.nr_seq_resultado  	= y.nr_sequencia
		and 	a.nr_seq_evolucao	= nr_seq_evolucao_p
		and	y.nr_seq_item		= nr_seq_item_w;

		ds_retorno_w	:=  ds_retorno_w  || ds_item_w || ': ' || ds_resultado_w || '   ';
		end;
	end if;
