CREATE OR REPLACE FUNCTION obter_resultado_phq9 ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_interesse_w		bigint;
ie_deprimido_w		bigint;
ie_sono_w		bigint;
ie_cansado_w		bigint;
ie_apetite_w		bigint;
ie_fracasso_w		bigint;
ie_concentrar_w		bigint;
ie_movimentar_w		bigint;
ie_ferir_w		bigint;

qt_total_geral_w	smallint;

ds_retorno_w		varchar(50) := '';


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	coalesce(max(a.ie_interesse),0),
		coalesce(max(a.ie_deprimido),0),
		coalesce(max(a.ie_sono),0),
		coalesce(max(a.ie_cansado),0),
		coalesce(max(a.ie_apetite),0),
		coalesce(max(a.ie_fracasso),0),
		coalesce(max(a.ie_concentrar),0),
		coalesce(max(a.ie_movimentar),0),
		coalesce(max(a.ie_ferir),0),
		coalesce(max(a.qt_pontuacao),0)
	into STRICT	ie_interesse_w,
		ie_deprimido_w,
		ie_sono_w,
		ie_cansado_w,
		ie_apetite_w,
		ie_fracasso_w,
		ie_concentrar_w,
		ie_movimentar_w,
		ie_ferir_w,
		qt_total_geral_w
	from	escala_phq9 a
	where	a.nr_sequencia = nr_sequencia_p;

	if (qt_total_geral_w <= 4) then
		ds_retorno_w := substr(obter_desc_expressao(347434),1,50);
	elsif (qt_total_geral_w <= 9) then
		ds_retorno_w := substr(obter_desc_expressao(874516),1,50);
	end if;

	if ((qt_total_geral_w >= 10) or (ie_interesse_w >= 2 or ie_deprimido_w >= 2) or (ie_ferir_w >= 1)) then
		ds_retorno_w := substr(obter_desc_expressao(874518),1,50);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
