SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resultado_sla_new_kpi ( dt_referencia_p timestamp, nr_seq_gerencia_p bigint default null, nr_seq_grupo_p text default null) RETURNS bigint AS $body$
DECLARE


qt_sla_w			double precision;
qt_response_breached_w		bigint;
qt_resolution_breached_w	bigint;
qt_total_w			double precision;
qt_sla_breached_w		bigint;
dt_ref_mes_w		timestamp;
dt_fim_mes_w		timestamp;


BEGIN
  qt_sla_w := 100;
	dt_ref_mes_w	:= trunc(dt_referencia_p,'month');
	dt_fim_mes_w	:= last_day(dt_ref_mes_w) + 86399/86400;

	select  count(status_sla_resolution) qtde
	into STRICT	qt_resolution_breached_w
	from    sla_informations
	where   extract_date between dt_ref_mes_w and dt_fim_mes_w
	and     upper(status_sla_resolution) = upper('breached')
	and     upper(philips_classification) = upper('defeito')
	and		develop_management_id <> 68
	and (coalesce(nr_seq_gerencia_p::text, '') = '' or develop_management_id = nr_seq_gerencia_p)
	and (coalesce(nr_seq_grupo_p::text, '') = '' or develop_group = nr_seq_grupo_p);

  	select  count(status_sla_response)
	into STRICT	qt_response_breached_w
	from    sla_informations
	where   extract_date between dt_ref_mes_w and dt_fim_mes_w
	and     upper(status_sla_response) = upper('breached')
	and     upper(philips_classification) = upper('defeito')
	and		develop_management_id <> 68
	and (coalesce(nr_seq_gerencia_p::text, '') = '' or develop_management_id = nr_seq_gerencia_p)
	and (coalesce(nr_seq_grupo_p::text, '') = '' or develop_group = nr_seq_grupo_p);

  select  count(status_sla_response)
	into STRICT	qt_total_w
	from    sla_informations
	where   extract_date between dt_ref_mes_w and dt_fim_mes_w
	and     upper(philips_classification) = upper('defeito')
	and     develop_management_id <> 68 -- GERENCIA ICAP
	and (coalesce(nr_seq_gerencia_p::text, '') = '' or develop_management_id = nr_seq_gerencia_p)
	and (coalesce(nr_seq_grupo_p::text, '') = '' or develop_group = nr_seq_grupo_p);
	qt_total_w := qt_total_w * 2; -- uma vez que, contamos 2 slas por ordem de servi√ßo.
	qt_sla_breached_w := qt_response_breached_w + qt_resolution_breached_w;

	if	(((qt_response_breached_w <> 0) or (qt_resolution_breached_w <> 0)) and (qt_total_w <> 0)) then
		qt_sla_w := 100 - ((qt_sla_breached_w * 100) / qt_total_w);
	end if;

return	qt_sla_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
