CREATE OR REPLACE FUNCTION obter_result_ambc (qt_amb_c_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	 varchar(255) := '';			

BEGIN

if (qt_amb_c_p > 15) then
	ds_retorno_w 	:= 	substr(obter_desc_expressao(294264),1,255);	--'Normal'
elsif (qt_amb_c_p between 5 and 15) then	
	ds_retorno_w 	:= 	substr(obter_desc_expressao(948262),1,255);	--'Desnutrição leve/moderada';
elsif (qt_amb_c_p < 5) then	
	ds_retorno_w 	:= 	substr(obter_desc_expressao(492459),1,255);	--'Desnutrição grave';
end if;

return	ds_retorno_w;

end;
$body$
