SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_result_cincinnati (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_pontuacao_w	bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	CASE WHEN coalesce(ie_fraqueza_facial,'N')='S' THEN 1  ELSE 0 END  +
		CASE WHEN coalesce(ie_fraqueza_membros_sup,'N')='S' THEN 1  ELSE 0 END  +
		CASE WHEN coalesce(ie_alteracao_fala,'N')='S' THEN 1  ELSE 0 END
	into STRICT	qt_pontuacao_w
	from	escala_cincinnati
	where	nr_sequencia = nr_sequencia_p;

	if (qt_pontuacao_w >= 1) then
		return 'Suspeita de AVCI';
	else
		return obter_desc_expressao(284052);
	end if;
else
	return '';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
