CREATE OR REPLACE FUNCTION obter_result_cot_fornec_item ( nr_seq_item_cot_fornec_p bigint) RETURNS bigint AS $body$
DECLARE


vl_preco_w			double precision;
vl_resultado_w			double precision;
vl_preco_liquido_w			double precision;
qt_material_w			double precision;


BEGIN

select	vl_preco,
	vl_preco_liquido,
	qt_material
into STRICT	vl_preco_w,
	vl_preco_liquido_w,
	qt_material_w
from	cot_compra_forn_item
where	nr_sequencia		= nr_seq_item_cot_fornec_p;

if (vl_preco_w = 0) then
	vl_resultado_w	:= 0;
else
	vl_resultado_w	:= ((vl_preco_w * qt_material_w) - vl_preco_liquido_w);
end if;

return	vl_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
