CREATE OR REPLACE FUNCTION obter_result_delirium_md ( qt_idade_p bigint, qt_pontos_p bigint ) RETURNS varchar AS $body$
DECLARE

  qt_pontos_w smallint;
  ds_retorno_w varchar(50);

BEGIN
  qt_pontos_w := qt_pontos_p;
