CREATE OR REPLACE FUNCTION obter_result_delirium ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

  qt_idade_w	smallint;
  qt_pontos_w	smallint;
  ds_retorno_w varchar(50);
  sql_w varchar(250);

BEGIN

  select	coalesce(qt_idade,0),
      (CASE WHEN coalesce(ie_abuso_alcool,'N')='S' THEN 1  ELSE 0 END  +
      CASE WHEN coalesce(ie_disfucao_cognitiva_prev,'N')='S' THEN 1  ELSE 0 END  +
      CASE WHEN coalesce(ie_doenca_grave,'N')='S' THEN 1  ELSE 0 END  +
      CASE WHEN coalesce(ie_dist_metab_operat,'N')='S' THEN 1  ELSE 0 END  +
      CASE WHEN coalesce(ie_cirurgia_aneurisma,'N')='S' THEN 1  ELSE 0 END  +
      CASE WHEN coalesce(ie_cirurgia_toraxica,'N')='S' THEN 1  ELSE 0 END )
  into STRICT	qt_idade_w,
      qt_pontos_w
  from	escala_risco_delirium
  where	nr_sequencia	=	nr_sequencia_p;

    begin
      sql_w := 'CALL OBTER_RESULT_DELIRIUM_MD(:1, :2) INTO :ds_retorno_w';
      EXECUTE sql_w
        USING IN qt_idade_w,
              IN qt_pontos_w,
              OUT ds_retorno_w;
    exception
      when others then
        ds_retorno_w := null;
      end;

    return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
