CREATE OR REPLACE FUNCTION obter_result_elpo (nr_pontuacao_p bigint) RETURNS varchar AS $body$
BEGIN

if ((nr_pontuacao_p IS NOT NULL AND nr_pontuacao_p::text <> '') or nr_pontuacao_p >= 7) then
	if (nr_pontuacao_p <= 19) then
		--388366 Risco Menor
		return wheb_mensagem_pck.get_texto(388366);
	elsif (nr_pontuacao_p >= 20) then
		--388367 Risco Maior
		return wheb_mensagem_pck.get_texto(388367);
	end if;
else
 	return '';
end if;

end;
$body$
