CREATE OR REPLACE FUNCTION obter_result_escala_braden_q (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(255);


BEGIN

select 	to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss') || ' ' || substr(obter_resultado_braden_q(qt_ponto),1,255)
into STRICT	ds_resultado_w
from 	atend_escala_braden_q e
where 	nr_atendimento 	= nr_atendimento_p
and   	nr_sequencia	= (SELECT max(x.nr_sequencia)
			  from atend_escala_braden_q x
			  where e.nr_atendimento = x.nr_atendimento
			  and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> ''));
return	ds_resultado_w;

end;
