CREATE OR REPLACE FUNCTION obter_result_escala_strong (qt_total_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';


BEGIN

if (qt_total_p	 = 0) then
	ds_retorno_w	:= 'Baixo risco (0 pontos) - Nenhuma intervenção nutricional é necessária. Checar o peso regularmente e avaliar o risco nutricional semanalmente (ou conforme protocolo hospitalar).';
elsif (qt_total_p between 1 and 3) then
	ds_retorno_w	:= 'Médio risco (1-3 pontos) - Considerar intervenção nutricional. Checar o peso duas vezes por semana e avaliar o risco nutricional semanalmente. Se necessário consultar especialista/médico para diagnóstico completo.';
elsif (qt_total_p between 4 and 5) then
	ds_retorno_w	:= 'Alto risco (4-5 pontos) - Consultar especialista/médico para diagnóstico completo; consultar médico/nutricionista para orientação nutricional individualizada e acompanhamento. Considerar prescrição de suplemento enquanto aguarda confirmação do status.';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
