CREATE OR REPLACE FUNCTION obter_result_gold ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(255);
ie_grupo_w			varchar(1);
ie_gold_w			varchar(3);


BEGIN

Select 	max(ie_gold),
		max(ie_grupo)
into STRICT	ie_gold_w,
		ie_grupo_w
from 	escala_gold
where	nr_sequencia = nr_sequencia_p;


If (ie_gold_w IS NOT NULL AND ie_gold_w::text <> '') then

	ds_resultado_w := 'GOLD '||ie_gold_w||' , '||obter_valor_dominio(8631,ie_grupo_w);

end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
