CREATE OR REPLACE FUNCTION obter_result_item_radio_chec ( nr_sequencia_p bigint, nr_seq_item_p bigint, nr_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_index_w		smallint;
nr_seq_result_w		bigint;
nr_seq_item_result_w	bigint;
nr_retorno_w		smallint:= -1;

c01 CURSOR FOR
SELECT	nr_sequencia,
		row_number() OVER () - 1
from (
		SELECT	nr_sequencia
		from	srpa_item_result
		where	nr_seq_item = nr_seq_item_p
		order by nr_sequencia) alias0;


BEGIN

select	coalesce(max(b.nr_seq_result),-1)
into STRICT	nr_seq_result_w
from 	atend_aval_srpa a,
	srpa_aval_item b
where 	a.nr_sequencia 	= b.nr_seq_avaliacao
and	a.nr_sequencia 	= nr_sequencia_p
and	b.nr_seq_item	= nr_seq_item_p;

open c01;
loop
fetch c01 into
	nr_seq_item_result_w,
	nr_index_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (nr_seq_item_result_w = nr_seq_result_w) then
		nr_retorno_w := nr_index_w;
	end if;
	end;
end loop;
close c01;

return 	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
