SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_result_lab_lme ( cd_pessoa_fisica_p text, nr_seq_exame_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_prescricao_w			bigint;
nr_seq_prescr_w			bigint;
qt_resultado_w			double precision;
nr_seq_resultado_w		bigint;
dt_resultado_prescr_w		timestamp;
nr_seq_resultado_s_prescr_w	bigint;
dt_resultado_s_prescr_w		timestamp;
nr_seq_result_item_w		bigint;

ie_comando_w			varchar(1);


BEGIN 
 
/* Pegar o último resultado com prescrição */
 
begin 
select	coalesce(max(d.nr_seq_resultado),0) 
into STRICT	nr_seq_resultado_w 
from  exame_lab_resultado d, 
    exame_lab_result_item c, 
	prescr_procedimento b, 
	prescr_medica a 
where	b.nr_prescricao		= a.nr_prescricao 
and	d.nr_prescricao 	= b.nr_prescricao 
and	c.nr_seq_prescr		= b.nr_sequencia 
and	d.nr_seq_resultado	= c.nr_seq_resultado 
and	c.nr_seq_exame  	= nr_seq_exame_p 
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	(c.dt_aprovacao IS NOT NULL AND c.dt_aprovacao::text <> '');
exception 
	when others then 
	nr_seq_resultado_w	:= 0;
end;
 
/* Pega a prescrição e data do resultado da prescrição */
 
if (nr_seq_resultado_w > 0) then 
	select	nr_prescricao, 
		dt_resultado 
	into STRICT	nr_prescricao_w, 
		dt_resultado_prescr_w 
	from	exame_lab_resultado 
	where	nr_seq_resultado	= nr_seq_resultado_w;
end if;
 
/* Pega o último resultado sem prescrição */
 
select	coalesce(max(a.nr_seq_resultado),0) 
into STRICT	nr_seq_resultado_s_prescr_w 
from	exame_lab_result_item b, 
	exame_lab_resultado a 
where	a.nr_seq_resultado	= b.nr_seq_resultado 
and	coalesce(a.nr_prescricao::text, '') = '' 
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
and	(a.nr_seq_protocolo IS NOT NULL AND a.nr_seq_protocolo::text <> '') 
and	b.nr_seq_exame		= nr_seq_exame_p 
and	a.nr_atendimento in (	SELECT	x.nr_atendimento 
		from	atendimento_paciente x 
		where	x.cd_pessoa_fisica	= cd_pessoa_fisica_p);
 
/* Pega a data do resultado sem prescricao */
 
if (nr_seq_resultado_s_prescr_w > 0) then 
	select	dt_resultado 
	into STRICT	dt_resultado_s_prescr_w 
	from	exame_lab_resultado 
	where	nr_seq_resultado	= nr_seq_resultado_s_prescr_w;
end if;
 
if (dt_resultado_prescr_w IS NOT NULL AND dt_resultado_prescr_w::text <> '') or (dt_resultado_s_prescr_w IS NOT NULL AND dt_resultado_s_prescr_w::text <> '') then 
	if (dt_resultado_prescr_w IS NOT NULL AND dt_resultado_prescr_w::text <> '') and (dt_resultado_s_prescr_w IS NOT NULL AND dt_resultado_s_prescr_w::text <> '') then 
		if (dt_resultado_prescr_w >= dt_resultado_s_prescr_w) then 
			ie_comando_w	:= 'P';
		else 
			ie_comando_w	:= 'D';
		end if;
	elsif (dt_resultado_prescr_w IS NOT NULL AND dt_resultado_prescr_w::text <> '') then 
		ie_comando_w		:= 'P';
	elsif (dt_resultado_s_prescr_w IS NOT NULL AND dt_resultado_s_prescr_w::text <> '') then 
		ie_comando_w		:= 'D';
	end if;
end if;
 
/* De acordo com o comando 
	P -> Prescrição 
	D -> Digitado 
  Pega o resultado do exame */
 
 
if (ie_comando_w = 'P') then 
	 
	/* Pegar a sequência da prescrição do paciente */
 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_prescr_w 
	from	prescr_procedimento 
	where	nr_seq_exame		= nr_seq_exame_p 
	and	nr_prescricao		= nr_prescricao_w;
 
	/* Pegar o resultado do exame */
 
	select	obter_valor_ds_resultado_exame(nr_seq_exame_p, nr_seq_prescr_w, nr_prescricao_w, 'V') 
	into STRICT	qt_resultado_w 
	;
 
elsif (ie_comando_w = 'D') then 
 
	/* Obter a sequência do item do resultado */
 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_result_item_w 
	from	exame_lab_result_item 
	where	nr_seq_resultado	= nr_seq_resultado_s_prescr_w 
	and	nr_seq_exame		= nr_seq_exame_p;
 
	/* Pegar o resultado do exame */
 
	select	obter_resultado_exame_ext(nr_seq_resultado_s_prescr_w, nr_seq_result_item_w) 
	into STRICT	qt_resultado_w 
	;
 
end if;
 
return	qt_resultado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_result_lab_lme ( cd_pessoa_fisica_p text, nr_seq_exame_p bigint) FROM PUBLIC;

