CREATE OR REPLACE FUNCTION obter_result_lab_panorama (nr_seq_exame_result_p bigint, ds_resultado_p text, qt_resultado_p bigint, pr_resultado_p bigint, nr_seq_prescr_p bigint, nr_prescricao_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_material_w	bigint;
ie_formato_resultado_w	varchar(3);


BEGIN

select 	MAX(a.nr_sequencia)
into STRICT	nr_seq_material_w
from	material_exame_lab a,
	prescr_procedimento b
where  	a.cd_material_exame = b.cd_material_exame
and	b.nr_prescricao = nr_prescricao_p
and	b.nr_sequencia = nr_seq_prescr_p;

ie_formato_resultado_w :=  obter_formato_result_exame(nr_seq_exame_p, nr_seq_material_w);

if (ie_formato_resultado_w = 'P') then
	ds_retorno_w	:= to_char(trunc(pr_resultado_p,4));
elsif (ie_formato_resultado_w = 'V') then
	ds_retorno_w	:= to_char(trunc(qt_resultado_p,4));
elsif	((ie_formato_resultado_w = 'D') or (ie_formato_resultado_w = 'S')) then
	ds_retorno_w	:= substr(ds_resultado_p,1,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
