CREATE OR REPLACE FUNCTION obter_result_man_sf (qt_pontuacao_p text) RETURNS varchar AS $body$
BEGIN
if (qt_pontuacao_p	>=12) then
	return	wheb_mensagem_pck.get_texto(356014); -- Estado nutricional normal
elsif (qt_pontuacao_p	>=8) and (qt_pontuacao_p	<12) then
	return wheb_mensagem_pck.get_texto(356015); -- Sob risco de desnutrição
elsif (qt_pontuacao_p	<8) then
	return	wheb_mensagem_pck.get_texto(356012); -- Desnutrido
else
	return null;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
