SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_result_man (qt_pontuacao_p text, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


total_w	smallint;


BEGIN

SELECT	count(*)
INTO STRICT total_w
FROM escala_man
WHERE nr_sequencia = nr_sequencia_p
AND coalesce(ie_sozinho::text, '') = ''
AND coalesce(ie_tres_medicamentos::text, '') = ''
AND coalesce(ie_escaras::text, '') = ''
AND coalesce(qt_refeicao::text, '') = ''
AND coalesce(ie_fruta::text, '') = ''
AND coalesce(qt_liquido::text, '') = ''
AND coalesce(ie_forma_alimentacao::text, '') = ''
AND coalesce(ie_pac_probl_nutricional::text, '') = ''
AND coalesce(ie_comp_idade::text, '') = ''
AND qt_pontuacao_triagem >= 12;

if (total_w > 0) then
	return Obter_Result_Man_sf(qt_pontuacao_p);
else
	if (qt_pontuacao_p	>=24) then
		return	wheb_mensagem_pck.get_texto(356007); -- Bem nutrido
	elsif (qt_pontuacao_p	>=17) and (qt_pontuacao_p	<24) then
		return wheb_mensagem_pck.get_texto(356008); -- Risco de desnutrição
	elsif (qt_pontuacao_p	<17) then
		return	wheb_mensagem_pck.get_texto(356012); -- Desnutrido
	else
		return null;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
