SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_result_pessoa_avaliacao ( nr_seq_avaliacao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


/*
O	Valor de domínio
D	Descrição
B	Booleano
C
H	Caption de groupbox
S	Lookup
T	Caption
X	Label
Z
*/
ie_resultado_w	varchar(2);
ds_resultado_w	varchar(4000);
cd_dominio_w	bigint;
qt_resultado_w	double precision;


BEGIN


select	a.ie_resultado
into STRICT	ie_resultado_w
from	med_item_avaliar a
where	a.nr_sequencia	= nr_seq_item_p;

/* Seleção simples */

if	ie_resultado_w in ('D','C','Z') then
	select	coalesce(max(a.ds_resultado),'')
	into STRICT	ds_resultado_w
	from	pessoa_avaliacao_result a
	where	a.nr_seq_avaliacao	= nr_seq_avaliacao_p
	and	a.nr_seq_item	= nr_seq_item_p;

/* Valor de domínio */

elsif (ie_resultado_w = 'O') then
	begin
   	select	coalesce(max(a.cd_dominio),0)
   	into STRICT	cd_dominio_w
   	from	Med_item_avaliar a
   	where	a.nr_sequencia	= nr_seq_item_p;

   	select	coalesce(max(a.ds_valor_dominio),'')
   	into STRICT	ds_resultado_w
   	from	pessoa_avaliacao_result b,
		med_valor_dominio a
   	where	a.nr_seq_dominio	= cd_dominio_w
	and	b.nr_seq_avaliacao	= nr_seq_avaliacao_p
	and	b.nr_seq_item	= nr_seq_item_p
   	and	a.vl_dominio	= b.qt_resultado;
	end;
elsif (ie_resultado_w = 'X') then
	select	coalesce(max(a.ds_item),'')
	into STRICT	ds_resultado_w
	from	med_item_avaliar a
	where	a.nr_sequencia	= nr_seq_item_p;

elsif (ie_resultado_w = 'B') then
	begin
	select	qt_resultado
	into STRICT	qt_resultado_w
	from	pessoa_avaliacao_result r,
		pessoa_fisica_avaliacao a,
		med_item_avaliar c
	where	r.nr_seq_avaliacao = a.nr_sequencia
	and	c.nr_sequencia = r.nr_seq_item
	and	r.nr_seq_avaliacao = nr_Seq_avaliacao_p
	and	c.nr_sequencia = nr_Seq_item_p;

	IF (coalesce(qt_resultado_w,0) = 0) THEN
		ds_resultado_w	:= 'N';
	ELSE
		ds_resultado_w	:= 'S';
	END IF;
	END;


elsif	not(ie_resultado_w in ('X','T','H')) then
	select	coalesce(max(a.ds_resultado),'')
	into STRICT	ds_resultado_w
	from	pessoa_avaliacao_result b,
		med_item_avaliar_res a
	where	a.nr_seq_item	= nr_seq_item_p
	and	b.nr_seq_avaliacao	= nr_seq_avaliacao_p
	and	b.nr_seq_item	= nr_seq_item_p
	and	a.nr_seq_res	= b.qt_resultado;
end if;

RETURN	ds_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_result_pessoa_avaliacao ( nr_seq_avaliacao_p bigint, nr_seq_item_p bigint) FROM PUBLIC;

