CREATE OR REPLACE FUNCTION obter_result_variable_dose ( cd_pessoa_fisica_p text, cd_material_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE

	
nr_seq_exame_w		material_variable_dose.nr_seq_exame%type;
ds_resultado_w		varchar(10);

C01 CURSOR FOR
	SELECT	nr_seq_exame
	from	material_variable_dose
	where	cd_material = cd_material_p
	and	coalesce(ie_situacao, 'I') = 'A';


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	open C01;
	loop
	fetch C01 into	
		nr_seq_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

			if (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then
		
				select	obter_resultado_dia_lab(cd_pessoa_fisica_p, dt_referencia_p, nr_seq_exame_w)
				into STRICT	ds_resultado_w
				;
				
				if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') and (ds_resultado_w <> 'NA') then
					return ds_resultado_w;
				end if;
			
			end if;

		end;
	end loop;
	close C01;
	
end if;

return ds_resultado_w;

end;
$body$
