CREATE OR REPLACE FUNCTION obter_resul_nrs (qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_resultado_w	varchar(4000);

BEGIN

if (qt_pontuacao_p	>= 3) then
	ds_resultado_w	:= wheb_mensagem_pck.get_texto(309242); -- O paciente está em risco nutricional e um plano nutricional é iniciado
else
	ds_resultado_w	:= wheb_mensagem_pck.get_texto(309243); -- Reavaliar o paciente semanalmente. Se o paciente realizará grande cirurgia, um plano de cuidado nutricional deve ser considerado para evitar estado de risco associado.
end if;


return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_resul_nrs (qt_pontuacao_p bigint) FROM PUBLIC;

