SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resumo_diaria (ie_tipo_diaria_p bigint, ie_tipo_valor_p bigint, nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


/* ie_tipo_diaria_p   1-Normais 2-UTI 9-Todas */

/* ie_tipo_valor_p    1-qtd diariais 2-vlr diarias */

vl_retorno_w 	double precision;



BEGIN

vl_retorno_w := 0;


/* diarias normais */

if (ie_tipo_diaria_p	= 1) then
	begin
	select	CASE WHEN ie_tipo_valor_p=1 THEN sum(a.qt_procedimento) WHEN ie_tipo_valor_p=2 THEN sum(a.vl_procedimento)  ELSE 0 END
	into STRICT	vl_retorno_w
	from	setor_atendimento c,
		procedimento b,
		procedimento_paciente a
	where	a.nr_interno_conta		= nr_interno_conta_p
	and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
	and	coalesce(a.nr_seq_proc_pacote::text, '') = ''
	and	a.cd_procedimento			= b.cd_procedimento
	and	a.ie_origem_proced		= b.ie_origem_proced
	and	b.ie_classificacao		= '3'
	and	a.cd_setor_atendimento	= c.cd_setor_atendimento
	and	c.cd_classif_setor		<> '4';
	end;
end if;

/* diarias UTI */

if (ie_tipo_diaria_p	= 2) then
	begin
	select	CASE WHEN ie_tipo_valor_p=1 THEN sum(a.qt_procedimento) WHEN ie_tipo_valor_p=2 THEN sum(a.vl_procedimento)  ELSE 0 END
	into STRICT	vl_retorno_w
	from	setor_atendimento c,
		procedimento b,
		procedimento_paciente a
	where	a.nr_interno_conta		= nr_interno_conta_p
	and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
	and	coalesce(a.nr_seq_proc_pacote::text, '') = ''
	and	a.cd_procedimento		= b.cd_procedimento
	and	a.ie_origem_proced		= b.ie_origem_proced
	and	b.ie_classificacao		= '3'
	and	a.cd_setor_atendimento	= c.cd_setor_atendimento
	and	c.cd_classif_setor		= '4';
	end;
end if;

/* diarias Todas */

if (ie_tipo_diaria_p	= 9) then
	begin
	select	CASE WHEN ie_tipo_valor_p=1 THEN sum(a.qt_procedimento) WHEN ie_tipo_valor_p=2 THEN sum(a.vl_procedimento)  ELSE 0 END
	into STRICT	vl_retorno_w
	from	procedimento b,
		procedimento_paciente a
	where	a.nr_interno_conta		= nr_interno_conta_p
	and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
	and	coalesce(a.nr_seq_proc_pacote::text, '') = ''
	and	a.cd_procedimento		= b.cd_procedimento
	and	a.ie_origem_proced		= b.ie_origem_proced
	and	b.ie_classificacao		= '3';
	end;
end if;

return 	vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_resumo_diaria (ie_tipo_diaria_p bigint, ie_tipo_valor_p bigint, nr_interno_conta_p bigint) FROM PUBLIC;

