CREATE OR REPLACE FUNCTION obter_resumo_parcela_cartao (nr_seq_parcela_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p

G	Geral
R	Redecard
C	Cielo

*/
ds_retorno_w			varchar(255);
nr_seq_extrato_parcela_w	bigint;


BEGIN

if	((ie_opcao_p = 'G') and (coalesce(ds_retorno_w::text, '') = '')) or (ie_opcao_p = 'R') then

	select	max(a.nr_seq_extrato_parcela)
	into STRICT	nr_seq_extrato_parcela_w
	from	movto_cartao_cr_parcela a
	where	a.nr_sequencia	= nr_seq_parcela_p;

	select	substr(obter_resumo_movto_redecard(nr_seq_extrato_parcela_w),1,255) nr_resumo
	into STRICT	ds_retorno_w
	;

end if;

if	((ie_opcao_p = 'G') and (coalesce(ds_retorno_w::text, '') = '')) or (ie_opcao_p = 'C') then

	select	max(a.nr_resumo)
	into STRICT	ds_retorno_w
	from	extrato_cartao_cr_movto a
	where	a.nr_seq_parcela	= nr_seq_parcela_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
