CREATE OR REPLACE FUNCTION obter_res_vol_tot_solucao_md ( ie_campo_p bigint, ie_vel_conforme_etapa_p text, qt_solucao_total_p bigint, qt_gotas_p bigint, qt_tempo_aplicacao_p bigint, qt_tempo_p bigint, nr_etapas_p bigint, qt_hora_fase_p bigint, qt_dosagem_p bigint, ie_arredonda_p text, ie_tipo_dosagem_p text, ie_utiliza_disp_inf_p text ) RETURNS bigint AS $body$
DECLARE


    resultado_w   double precision;
    nr_etapas_w   bigint;
    qt_solucao_w  double precision;

BEGIN
    IF ( ie_campo_p = 1 ) THEN
        IF ( ie_vel_conforme_etapa_p = 'N' ) THEN
            resultado_w := dividir_md(dividir_md((round((qt_solucao_total_p)::numeric, 1) * qt_gotas_p), qt_tempo_aplicacao_p), qt_tempo_p);

        ELSE
            nr_etapas_w := nr_etapas_p;
            IF ( nr_etapas_p = 0 ) THEN
                nr_etapas_w := 1;
            END IF;
            qt_solucao_w := dividir_sem_round_md(qt_solucao_total_p , nr_etapas_w);
            resultado_w := dividir_md(dividir_md((round((qt_solucao_w)::numeric, 1) * qt_gotas_p), qt_hora_fase_p), qt_tempo_p);

        END IF;
    ELSIF ( ie_campo_p = 2 ) THEN
        BEGIN
            resultado_w := round(dividir_md((qt_dosagem_p * qt_tempo_p * qt_tempo_aplicacao_p), qt_gotas_p));

            resultado_w := ( round(dividir_sem_round_md(resultado_w , 100)) * 100 );
        END;
    ELSIF ( ie_campo_p = 3 ) THEN
        resultado_w := round(dividir_md(dividir_md((qt_solucao_total_p * qt_gotas_p), qt_dosagem_p), qt_tempo_p));
    END IF;

    IF ( resultado_w = 0 ) THEN
        resultado_w := NULL;
    END IF;
    IF (
        ( ie_arredonda_p = 'S' )
        AND ( ie_tipo_dosagem_p = 'GTM' )
        AND ( ie_utiliza_disp_inf_p = 'N' )
    ) OR ( ie_arredonda_p = 'T' ) THEN
        resultado_w := round(resultado_w);
    END IF;

    RETURN resultado_w;
END;
