CREATE OR REPLACE FUNCTION obter_retornos_guia (nr_interno_conta_p bigint, cd_autorizacao_p text, nr_seq_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
nr_seq_retorno_w	bigint;


c01 CURSOR FOR
SELECT	b.nr_sequencia nr_sequencia
from	convenio_retorno_item a,
	convenio_retorno b
where	a.nr_seq_retorno	= b.nr_sequencia
and	b.ie_status_retorno	= 'F'
and	a.nr_interno_conta	= nr_interno_conta_p
and	a.cd_autorizacao	= cd_autorizacao_p
and	b.nr_sequencia		<> nr_seq_retorno_p
group by b.nr_sequencia
order by nr_sequencia;


BEGIN

ds_retorno_w		:= '';
open c01;
loop
fetch c01 into
	nr_seq_retorno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w	:= ds_retorno_w || nr_seq_retorno_w || ', ';

end loop;
close c01;

if (position(', '  ds_retorno_w) > 0) then
	ds_retorno_w		:= substr(ds_retorno_w, 1, length(ds_retorno_w) - 2);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
