CREATE OR REPLACE FUNCTION obter_retornos_lote_audit (nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(4000):= ' ';
nr_seq_retorno_w		varchar(255);

c01 CURSOR FOR
	SELECT	distinct
		w.nr_seq_retorno
	FROM convenio_retorno_item w, pessoa_juridica v, estabelecimento j, conta_paciente_ret_hist z
LEFT OUTER JOIN lote_audit_recurso k ON (z.nr_seq_lote_recurso = k.nr_sequencia)
WHERE k.cd_estabelecimento  = j.cd_estabelecimento AND j.cd_cgc			  = v.cd_cgc  AND z.nr_seq_ret_item	  = w.nr_sequencia and k.nr_sequencia		  = nr_seq_lote_p;


BEGIN

open c01;
loop
fetch	c01 into
	nr_seq_retorno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

if (ds_retorno_w = ' ') then
	ds_retorno_w := nr_seq_retorno_w;
else
	ds_retorno_w := ds_retorno_w ||', '|| nr_seq_retorno_w;
end if;

end loop;
close c01;

return	substr(ds_retorno_w,1,3999);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
