SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_retorno_conta (nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);/*Felipe Martini Alterei o tamanho do Campo para 2000 OS78374*/
nr_seq_retorno_w	bigint;

/*
ie_opcao_p
'1' - nÃºmero de todos os retornos que a conta esta
*/
c01 CURSOR FOR
SELECT	distinct nr_seq_retorno
from	convenio_retorno_item
where	nr_interno_conta	= nr_interno_conta_p;


BEGIN

ds_retorno_w		:= '';
if (ie_opcao_p = '1') then

	open c01;
	loop
	fetch c01 into
		nr_seq_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		ds_retorno_w	:= ds_retorno_w || nr_seq_retorno_w || ', ';

	end loop;
	close c01;

	if (position(', ' in ds_retorno_w) > 0) then
		ds_retorno_w		:= substr(ds_retorno_w, 1, length(ds_retorno_w) - 2);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
