CREATE OR REPLACE FUNCTION obter_retorno_guia ( nr_interno_conta_p bigint, cd_autorizacao_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao					*/

/*	P = Primeiro Retorno       		*/

/*	U = Ãšltimo Retorno			*/

nr_seq_retorno_w			bigint;


BEGIN

if (ie_opcao_p = 'P') then
	select	coalesce(min(b.nr_sequencia),0)
	into STRICT	nr_seq_retorno_w
	from 	convenio_retorno b,
		convenio_retorno_item a
	where	a.nr_seq_retorno	= b.nr_sequencia
	  and	b.ie_status_retorno	= 'F'
	  and	a.nr_interno_conta	= nr_interno_conta_p
	  and	a.cd_autorizacao	= cd_autorizacao_p;
elsif (ie_opcao_p = 'U') then
	select	coalesce(max(b.nr_sequencia),0)
	into STRICT	nr_seq_retorno_w
	from 	convenio_retorno b,
		convenio_retorno_item a
	where	a.nr_seq_retorno	= b.nr_sequencia
	  and	b.ie_status_retorno	= 'F'
	  and	a.nr_interno_conta	= nr_interno_conta_p
	  and	a.cd_autorizacao	= cd_autorizacao_p;
end if;

RETURN nr_seq_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
