SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_retorno_protocolo (nr_seq__protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
IE_OPCAO_P
'S'	Seq retorno
'DT'	Dt retorno
*/
ds_retorno_w		varchar(255);
nr_seq_retorno_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_retorno_w
from	convenio_retorno
where	nr_seq_protocolo	= nr_seq__protocolo_p;

if (coalesce(nr_seq_retorno_w::text, '') = '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_retorno_w
	from	convenio_retorno
	where	' ' || replace(nr_seq_prot_adic, ';', ' ') || ' ' like '% ' || nr_seq__protocolo_p || ' %'
	and	(nr_seq_prot_adic IS NOT NULL AND nr_seq_prot_adic::text <> '');

end if;

ds_retorno_w	:= null;

if (nr_seq_retorno_w IS NOT NULL AND nr_seq_retorno_w::text <> '') then
	if (ie_opcao_p	= 'S') then
		ds_retorno_w	:= nr_seq_retorno_w;
	elsif (ie_opcao_p	= 'DT') then
		select	max(dt_retorno)
		into STRICT	ds_retorno_w
		from	convenio_retorno
		where	nr_sequencia	= nr_seq_retorno_w;
	end if;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
