CREATE OR REPLACE FUNCTION obter_retorno_remessa ( nr_seq_pagto_p bigint, nr_seq_cobranca_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w	bigint;


BEGIN

if (nr_seq_pagto_p IS NOT NULL AND nr_seq_pagto_p::text <> '') then

	select	max(a.nr_sequencia)
	into STRICT	nr_seq_retorno_w
	from	banco_escritural a
	where	a.ie_remessa_retorno	= 'I'
	and	exists (SELECT	1
		from	titulo_pagar_escrit x
		where	x.nr_titulo	in (select	w.nr_titulo
			from	titulo_pagar_escrit w
			where	w.nr_seq_escrit	= nr_seq_pagto_p)
		and	x.nr_seq_escrit	= a.nr_sequencia);

end if;

RETURN nr_seq_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
