CREATE OR REPLACE FUNCTION obter_revisao_servico_pad ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE

									
ds_retorno_w			varchar(4000);


BEGIN

select count(*)
into STRICT 	ds_retorno_w
from 	hc_paciente_servico a,
		paciente_home_care	 b
where a.nr_seq_pac_home_care = b.nr_sequencia
and 	b.nr_sequencia = nr_sequencia_p
and 	(a.dt_revisao IS NOT NULL AND a.dt_revisao::text <> '')
and		a.dt_revisao <= clock_timestamp();

if (ds_retorno_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';	
end if;
		
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
