CREATE OR REPLACE FUNCTION obter_rn_atendimento (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w		bigint;
ds_retorno_w	varchar(3);


BEGIN
select	count(*)
into STRICT		qt_reg_w
from		nascimento
where	nr_atendimento = nr_atendimento_p;

if (qt_reg_w = 1) then
	select	max(nr_sequencia)
	into STRICT		ds_retorno_w
	from		nascimento
	where	nr_atendimento	= nr_atendimento_p;
end if;

return ds_retorno_w;

end;
