CREATE OR REPLACE FUNCTION obter_rotina_digito_usuario ( cd_convenio_p bigint, cd_categoria_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

C01 CURSOR FOR
	SELECT	ds_rotina_digito
	from	conv_regra_rotina_digito
	where	cd_categoria = cd_categoria_p
	and	cd_convenio = cd_convenio_p
	and 	coalesce(ie_situacao,'A') = 'A'
	order by cd_categoria;


BEGIN


open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w:=ds_retorno_w;
	end;
end loop;
close C01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
