CREATE OR REPLACE FUNCTION obter_sae_protocolo_liberado ( nr_seq_protocolo_p bigint, nr_Atendimento_p bigint default 0, nr_seq_modelo_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		   varchar(1) := 'S';
cd_perfil_w		       bigint;
qt_reg_w		       bigint;
cd_estabelecimento_w   smallint;
cd_setor_atendimento_w integer := 0;


BEGIN
cd_perfil_w	:= coalesce(obter_perfil_ativo, 0);
if (nr_seq_protocolo_p > 0) then
	begin

	if (nr_atendimento_p > 0) then
		cd_setor_atendimento_w := coalesce(obter_setor_atendimento(nr_atendimento_p), 0);
	end if;

	select	count(*)
	into STRICT	qt_reg_w
	from	pe_protocolo_regra
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
	
	cd_estabelecimento_w := coalesce(wheb_usuario_pck.get_cd_estabelecimento, 0);
	
	if (qt_reg_w > 0) then

		select count(*)
		into STRICT   qt_reg_w
		from   pe_protocolo_regra
		where  nr_seq_protocolo	= nr_seq_protocolo_p
		and	   coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
		and	   coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w
		and    coalesce(cd_setor_atendimento, cd_setor_atendimento_w) = cd_setor_atendimento_w
		and	   coalesce(nr_seq_modelo, coalesce(nr_seq_modelo_p, 0)) = coalesce(nr_seq_modelo_p, 0);

		if (qt_reg_w = 0) then
			ie_liberado_w	:= 'N';
		end if;
	end if;
	end;
end if;

return	ie_liberado_w;

end;
$body$
