CREATE OR REPLACE FUNCTION obter_sala_computador (nm_computador_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_interno_w	bigint;


BEGIN

select  max(nr_seq_interno)
into STRICT	nr_seq_interno_w
from    computador
where   coalesce(ie_situacao,'A') = 'A'
